import { ADD_TWEET, LIKE_TWEET } from '../actions/actions'


export default function tweetsReducer(state = [], action) {
    switch (action.type) {
        case ADD_TWEET: {
            let { id, username, picture, tweetData, isLiked } = action.tweet;
            return [...state, { id, username, picture, tweetData, isLiked }];
        }
        case LIKE_TWEET:{}
            //const name = action.tweet.id;
            //const like = action.tweet.isLiked;
            let { id, isLiked } = action.tweet;
            return [...state, { id, username, picture, tweetData, isLiked }];

        default:
            return state;
    }
}
