import { takeEvery, put } from 'redux-saga/effects';
import { LIKE_TWEET } from '../actions/tweetsActions';
import { ADD_NOTIFICATION } from '../actions/notificationsActions';

function* watchLikeTweet(action) {
    yield takeEvery(LIKE_TWEET, addNotification);
}

function* addNotification() {
    yield put(ADD_NOTIFICATION, addNotification);
}

export default function* rootSaga() {
    yield watchLikeTweet();
}
