import { ADD_TWEET } from '../actions/actions'

const initialState = {
    tweets: []
}

export default function tweetsReducer(state = initialState, action) {
    switch (action.type) {
        case ADD_TWEET:
            return [...state.tweets, action.payload.username, action.payload.picture, ];
        default:
            return state;
    }
}
