import { ADD_TWEET } from '../actions/actions'

const initialState = {
    tweets: []
}

export default function tweetsReducer(state = initialState, action) {
    switch (action.type) {
        case ADD_TWEET:
            const { data } = action.payload;
            console.log(action);
            console.log(data);
            console.log({ ...state, data });
            return { ...state, data };
        default:
            return state;
    }
}
