import React from 'react';
import TweetBtn from './TweetBtn';

function PostTweet(props) {
    return <div className="news-feed-header">
        <div className="news-feed-header-title title">Home</div>
        <div className="tweet-item bordered">
            <div className="tweet-item-pic">
                <img src={props.user.picture} />
            </div>
            <div className="tweet-item-text-input">
                <input id="tweet-input" className="tweet-item-edit-text" placeholder="What's happening?"></input>
            </div>
        </div>
        <TweetBtn size="small-btn" content="Tweet" />
    </div>
}

createNewTweet = () => {
    let tweetData = document.getElementById("tweet-input").value;
    let newTweet = {...props.user, tweetData :tweetData}
}

export default PostTweet;
