import { takeEvery } from 'redux-saga/effects';
import { addNotification } from '../actions/notificationsActions';
import { likeTweet } from '../actions/tweetsActions';
import { LIKE_TWEET } from '../actions/tweetsActions';

function* watchLikeTweet() {
    yield takeEvery(LIKE_TWEET, likeTweet);
    yield takeEvery(LIKE_TWEET, addNotification);
}


export default function* rootSaga() {
    yield watchLikeTweet();
}
