import { ADD_TWEET } from '../actions/actions'

const initialState = {
    tweets: []
}

export default function tweetsReducer(state = initialState, action) {
    switch (action.type) {
        case ADD_TWEET:
            const { username, picture, tweetData } = action.tweet;
            console.log("ADD_TWEET");
            console.log({ state });
            return {tweets :[...state.tweets, { username, picture, tweetData }]};
        default:
            return state;
    }
}
