import React, { useState } from 'react';
import PostTweet from './PostTweet';
import TweetList from './TweetList';

function NewsFeed(props) {
    const [tweetList, setTweetList] = useState([]);
    //let tweetList = [];

    const callbackFunction = (childData) => {
        setTweetList(tweetList.push(childData));
        console.log(tweetList);
    }

    return <div className="news-feed bordered">
        <PostTweet user={props.user} callbackFunction={callbackFunction} />
        <div id="tweet-list" className="tweet-list">
        {tweetList.map((item) => { return <d pic={item.picture} username={item.username} content={item.tweetData} /> })}
        </div>
        <TweetList list={tweetList} />
    </div>
}


export default NewsFeed;
