import { ADD_TWEET } from '../actions/actions'

const initialState = {
    tweets: []
}

export default function tweetsReducer(state = initialState, action) {
    switch (action.type) {
        case ADD_TWEET:
            const { username, picture, tweetData } = action.payload;
            return [...state.tweets, 
                username, picture, tweetData];
        default:
            return state;
    }
}
