import React, { useState, useRef } from 'react';
import '../style/Login.css';
import { useHistory } from 'react-router-dom';
import Logo from '../common/Logo';
import TweetBtn from '../common/TweetBtn';


function Login() {
    const form = useRef();
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory();

    function handleNameChange(e) {
        setName(e.target.value);
    }
    function handlePasswordChange(e) {
        setPassword(e.target.value);
    }

    async function handleLogin(e) {
        try {
            console.log('handleLogin');
            e.preventDefault();
            history.push('/Home');
        } catch (e) {
        }
    }

    return (
        <div class="login-page">
            <div class="form">
                <form className="login-form" onSubmit={handleLogin} ref={form}>
                    <div class="login-header">
                        <Logo />
                        <h2 class="header"> Log in to Twitter</h2>
                    </div>
                    <input
                        placeholder="Phone, email, or username"
                        type="text"
                        onChange={handleNameChange}
                    />
                    <input
                        placeholder="Password"
                        type="password"
                        onChange={handlePasswordChange}
                    />

                    <TweetBtn content="Log in" size="x-large-btn" />
                    <p class="message">Not Registered? <a href="/Register">Register</a> </p>
                </form>
            </div>
        </div>
    );
}



export default Login;
