import { ADD_TWEET } from '../actions/actions'

const initialState = {
    tweets: []
}

export default function tweetsReducer(state = initialState, action) {
    switch (action.type) {
        case ADD_TWEET:
            const { tweet } = action.payload;
            console.log(action);
            console.log(tweet);
            console.log(state);
            return [...state, tweet];
        default:
            return state;
    }
}
