import { ADD_TWEET } from '../actions/actions'

const initialState = {
    tweets: []
}

export default function tweetsReducer(state = initialState, action) {
    switch (action.type) {
        case ADD_TWEET:
            return { isLoggedIn: true };
        case 'SIGN_UP':
            return { isLoggedIn: true };
        default:
            return state;
    }
}
