const initialState = {
    isLoggedIn: false
}

export default function loginReducer(state = initialState, action) {
    switch (action.type) {
        case 'LOGIN':
            return {isLoggedIn: true};
        case 'SIGN-UP':
            return true;
        default:
            return state;
    }
}
