import React, { useState, useRef } from 'react';
import '../style/Login.css';
import { useHistory } from 'react-router-dom';
import Logo from '../common/Logo';
import TweetBtn from '../common/TweetBtn';


function Login() {
    const form = useRef();
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory();

    function handleNameChange(e) {
        setName(e.target.value);
    }
    function handlePasswordChange(e) {
        setPassword(e.target.value);
    }

    function handleLogin() {
        try {
            history.push('/Home');
        } catch (e) {
            console.log(e);
        }
    }

    return (
        <div className="login-page">
            <div className="form">
                <form className="login-form" onSubmit={handleLogin()} ref={form}>
                    <div className="login-header">
                        <Logo />
                        <h2 className="header"> Log in to Twitter</h2>
                    </div>
                    <input className="login-input"
                        placeholder="Phone, email, or username"
                        type="text"
                        onChange={handleNameChange}
                    />
                    <input className="login-input"
                        placeholder="Password"
                        type="password"
                        onChange={handlePasswordChange}
                    />

                    <TweetBtn content="Log in" size="x-large-btn" />
                    <p className="message">Not Registered? <a href="/Register">Register</a> </p>
                </form>
            </div>
        </div>
    );
}



export default Login;
