import { ADD_TWEET, LIKE_TWEET } from '../actions/tweetsActions'


export default function tweetsReducer(state = [], action) {
    switch (action.type) {
        case ADD_TWEET:
            const { id, username, picture, tweetData, isLiked } = action.tweet;
            return [...state, { id, username, picture, tweetData, isLiked }];
        case LIKE_TWEET:
